mapper.insert:
insert into ack_account (id, clientId,
  balance,coin,remark, userId, createTime
  )
values (#{id,jdbcType=BIGINT}, #{clientId,jdbcType=INTEGER},
  #{balance,jdbcType=DECIMAL}, #{coin,jdbcType=DECIMAL}, #{remark,jdbcType=VARCHAR}, #{userId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT act.id, act.clientId, act.balance, act.coin, act.remark, act.createTime, act.userId, clt.`name` AS client_name, clt.phone AS client_phone, aur.surname AS user_surname, aur.`name`as user_name FROM ack_account act LEFT JOIN ack_client clt ON act.clientId = clt.id LEFT JOIN ack_user aur ON act.userId = aur.id
1 = 1
and act.clientId=#{condition.clientId}
and act.createTime=#{condition.createTime}
and act.id=#{condition.userId}
