mapper.select:
SELECT cu.id, cu.username, cu.type, cu.state, cu.email, cu.realName, cu.qq, cu.sex, cu.birthday, cu.mobile, cu.avatar, cu.address, cu.creator, cu.createtime, cu.modifier, cu.modifyTime, cu.deleteStatus, cu.departmentId, cd.id AS dept_id, cd.`name` AS dept_name FROM cms_user cu LEFT JOIN cms_department cd ON cu.departmentId = cd.id
1 = 1 and cu.deleteStatus = 0
and cu.username=#{condition.username}
and cu.email=#{condition.email}
and cu.departmentId=#{condition.departmentId}

select

from cms_user

    username=#{username}
	
		and email=#{email}
	
	
		and qq=#{qq}
	
	
		and mobile=#{mobile}
